import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [questionCount, setQuestionCount] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    const count = parseInt(questionCount);
    if (!count || count < 1 || count > 50) {
      setError('Please enter a valid number between 1 and 50');
      return;
    }

    // Store question count in localStorage and navigate to question creator
    localStorage.setItem('questionCount', count.toString());
    navigate('/create-questions');
  };

  const handleLogout = () => {
    localStorage.removeItem('jwt');
    localStorage.removeItem('questionCount');
    navigate('/');
  };

  return (
    <div className="container">
      <div className="card">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
          <h1 className="title" style={{ margin: 0 }}>üìù Create Quiz</h1>
          <button onClick={handleLogout} className="btn btn-secondary">
            Logout
          </button>
        </div>

        <p className="subtitle">
          How many questions would you like to create for your quiz?
        </p>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="questionCount">Number of Questions</label>
            <input
              type="number"
              id="questionCount"
              value={questionCount}
              onChange={(e) => setQuestionCount(e.target.value)}
              placeholder="Enter number of questions (1-50)"
              min="1"
              max="50"
              required
            />
          </div>

          {error && <div className="error">{error}</div>}

          <button type="submit" className="btn btn-primary btn-full">
            Start Creating Questions
          </button>
        </form>

        <div style={{ marginTop: '30px', padding: '20px', background: '#f7fafc', borderRadius: '10px' }}>
          <h3 style={{ color: '#2d3748', marginBottom: '15px' }}>üí° Tips:</h3>
          <ul style={{ color: '#4a5568', lineHeight: '1.6' }}>
            <li>Each question will have 4 multiple choice options</li>
            <li>You'll need to specify which option is correct</li>
            <li>Questions will be sent to the server individually</li>
            <li>Keep questions clear and concise</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
