import axios from 'axios';

// Create axios instance with base configuration
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:8080',
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add request interceptor to include JWT token and logging
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('jwt');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    // Log all API requests for debugging
    console.log(`ðŸš€ API Request [${config.method?.toUpperCase()}]:`, {
      url: config.url,
      baseURL: config.baseURL,
      fullUrl: `${config.baseURL}${config.url}`,
      data: config.data,
      params: config.params
    });
    
    return config;
  },
  (error) => {
    console.error('âŒ API Request Error:', error);
    return Promise.reject(error);
  }
);

// Add response interceptor for logging
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… API Response [${response.config.method?.toUpperCase()}]:`, {
      url: response.config.url,
      status: response.status,
      data: response.data
    });
    return response;
  },
  (error) => {
    console.error(`âŒ API Response Error [${error.config?.method?.toUpperCase()}]:`, {
      url: error.config?.url,
      status: error.response?.status,
      message: error.response?.data?.message || error.message,
      fullError: error.response?.data
    });
    return Promise.reject(error);
  }
);

// API functions
export const authAPI = {
  signup: (userData) => api.post('/api/auth/signup', userData),
  login: (credentials) => api.post('/api/auth/login', credentials),
};

export const quizAPI = {
  // Quiz operations
  getAllQuizzes: () => api.get('/api/quiz'),
  getQuiz: (quizId) => api.get(`/api/quiz/${quizId}`),
  createQuiz: (quizData) => api.post('/api/quiz', quizData),
  updateQuiz: (quizId, quizData) => api.post(`/api/quiz/${quizId}`, quizData),
  
  // Question operations
  getQuestions: (quizId) => api.get(`/api/quiz/${quizId}/question`),
  getQuestion: (quizId, questionId) => api.get(`/api/quiz/${quizId}/question/${questionId}`),
  createQuestion: (quizId, questionData) => api.post(`/api/quiz/${quizId}/question`, questionData),
  createOrUpdateQuestion: (quizId, questionId, questionData) => 
    api.post(`/api/quiz/${quizId}/question/${questionId}`, questionData),
};

// Legacy API for backward compatibility
export const gameAPI = {
  createGame: () => quizAPI.createQuiz({ title: 'New Quiz', description: 'Created from question creator' }),
  createQuestion: (gameId, questionId, questionData) => {
    // For legacy support, use the create question endpoint without specifying ID
    // The server will auto-assign the question ID
    console.log('Legacy createQuestion called:', { gameId, questionId, questionData });
    return quizAPI.createQuestion(gameId, questionData);
  },
};

export default api;
